import cv2 as cvimport numpy as npfrom matplotlib import pyplot as plt'''Face Detection using Haar Cascades'''face_cascade = cv.CascadeClassifier(    '/home/debadatta/.local/lib/python3.6/site-packages/cv2/data/haarcascade_frontalface_default.xml')eyes_cascade = cv.CascadeClassifier('/home/debadatta/.local/lib/python3.6/site-packages/cv2/data/haarcascade_eye.xml')image = cv.imread('/home/debadatta/Pictures/potuNeil3.jpg')grayImage = cv.cvtColor(image, cv.COLOR_BGR2GRAY)face = face_cascade.detectMultiScale(grayImage, 1.3, 5)for (x, y, w, h) in face:    cv.rectangle(image, (x, y), (x + w, y + h), (255, 0, 0), 2)    roi_gray = grayImage[y:y + h, x:x + w]    roi_color = grayImage[y:y + h, x:x + w]    # Now we find the faces in the image.    # If faces are found, it returns the positions of detected faces as Rect(x,y,w,h).    # ROI-> Region of Interest    # Once we get these locations, we can create a ROI for the face and apply eye detection on this ROI (since eyes are always on the face !!! ).    # eyes = eyes_cascade.detectMultiScale(roi_gray)    # for (ex, ey, ew, eh) in eyes:    #     cv.rectangle(roi_color, (ex, ey), (ex + ew, ey + eh), (0, 255, 0), 2)cv.imshow('image', image)cv.waitKey(0)cv.destroyAllWindows()